import React, { useState, useEffect } from "react";
import {
  Card,
  Container,
  Form,
  Button,
  Image,
  Row,
  Col,
  ListGroup,
} from "react-bootstrap";
import api from "../api/axiosInstance";
import { useAuth } from "../context/AuthContext";

const Profile = () => {
  const { user } = useAuth();

  const [profile, setProfile] = useState(null);
  const [name, setName] = useState("");
  const [bio, setBio] = useState("");
  const [qr, setQr] = useState("");
  const [otp, setOtp] = useState("");
  const [message, setMessage] = useState("");

  const [searchQuery, setSearchQuery] = useState("");
  const [searchResults, setSearchResults] = useState([]);
  const [myPosts, setMyPosts] = useState([]);

  const fetchProfile = async () => {
    const { data } = await api.get("/users/me");
    setProfile(data);
    setName(data.name || "");
    setBio(data.bio || "");

    // my posts
    const postsRes = await api.get(`/posts/user/${data._id}`);
    setMyPosts(postsRes.data);
  };

  useEffect(() => {
    fetchProfile();
    // eslint-disable-next-line
  }, []);

  const updateProfile = async (e) => {
    e.preventDefault();
    const { data } = await api.put("/users/me", { name, bio });
    setProfile(data);
    setMessage("Profile updated");
    setTimeout(() => setMessage(""), 2000);
  };

  const handleFileChange = async (e) => {
    const file = e.target.files[0];
    if (!file) return;
    const formData = new FormData();
    formData.append("image", file);
    const { data } = await api.post("/users/me/profile-pic", formData, {
      headers: { "Content-Type": "multipart/form-data" },
    });
    setProfile((prev) => ({ ...prev, profilePic: data.profilePic }));
  };

  const generate2FA = async () => {
    const { data } = await api.post("/auth/2fa/generate");
    setQr(data.qrDataUrl);
  };

  const enable2FA = async () => {
    const { data } = await api.post("/auth/2fa/enable", { otp });
    setMessage(data.message);
    setOtp("");
    fetchProfile();
  };

  const handleSearch = async (e) => {
    e.preventDefault();
    if (!searchQuery.trim()) return;
    const { data } = await api.get(
      `/users/search?q=${encodeURIComponent(searchQuery)}`
    );
    setSearchResults(data);
  };

  const follow = async (id) => {
    await api.post(`/users/${id}/follow`);
    await fetchProfile();
  };

  const unfollow = async (id) => {
    // if you don’t have this route yet, create it in backend, or toggle in same /follow
    await api.post(`/users/${id}/unfollow`);
    await fetchProfile();
  };

  const isFollowing = (id) =>
    profile?.following?.some((u) => u._id === id || u === id);

  if (!profile) return <Container>Loading...</Container>;

  return (
    <Container className="mt-3">
      <Row>
        {/* LEFT – Welcome card */}
        <Col md={4} className="mb-3">
          <Card>
            <Card.Body>
              <Card.Title className="mb-3">Welcome</Card.Title>
              <div className="d-flex flex-column align-items-center mb-3">
                {profile.profilePic && (
                  <Image
                    src={`http://localhost:5000${profile.profilePic}`}
                    roundedCircle
                    width={120}
                    height={120}
                    className="mb-2"
                  />
                )}
                <Form.Group className="w-100 text-center">
                  <Form.Control
                    type="file"
                    onChange={handleFileChange}
                    className="mb-2"
                  />
                  <Button
                    variant="secondary"
                    size="sm"
                    onClick={() => {}}
                    disabled
                  >
                    Upload/Edit Profile Picture
                  </Button>
                </Form.Group>
              </div>

              <Form onSubmit={updateProfile}>
                <Form.Group className="mb-2">
                  <Form.Label>Username</Form.Label>
                  <Form.Control
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                  />
                </Form.Group>
                <Form.Group className="mb-2">
                  <Form.Label>Email</Form.Label>
                  <Form.Control value={profile.email} disabled />
                </Form.Group>
                <Form.Group className="mb-3">
                  <Form.Label>Bio</Form.Label>
                  <Form.Control
                    as="textarea"
                    rows={2}
                    value={bio}
                    onChange={(e) => setBio(e.target.value)}
                  />
                </Form.Group>
                <Button type="submit" className="w-100 mb-3">
                  Save
                </Button>
              </Form>

              <Button
                variant="dark"
                className="w-100 mb-2"
                onClick={generate2FA}
              >
                Enable 2FA
              </Button>

              {qr && (
                <div className="text-center mb-3">
                  <p className="mb-1">Scan QR in Authenticator app</p>
                  <img src={qr} alt="2FA QR" style={{ maxWidth: "160px" }} />
                  <Form.Control
                    className="mt-2"
                    placeholder="Enter OTP"
                    value={otp}
                    onChange={(e) => setOtp(e.target.value)}
                  />
                  <Button size="sm" className="mt-2" onClick={enable2FA}>
                    Confirm
                  </Button>
                </div>
              )}

              {message && (
                <div className="text-success small mt-1">{message}</div>
              )}
            </Card.Body>
          </Card>
        </Col>

        {/* RIGHT – Search + Followers / Following */}
        <Col md={8} className="mb-3">
          <Card className="mb-3">
            <Card.Body>
              <Card.Title>Search Users</Card.Title>
              <Form onSubmit={handleSearch} className="d-flex gap-2 mb-3 mt-2">
                <Form.Control
                  placeholder="Search by username or email"
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                />
                <Button type="submit">Search</Button>
              </Form>

              <ListGroup>
                {searchResults.map((u) => (
                  <ListGroup.Item
                    key={u._id}
                    className="d-flex justify-content-between align-items-center"
                  >
                    <span>
                      {u.name} ({u.email})
                    </span>
                    {user && user._id !== u._id && (
                      <div>
                        {isFollowing(u._id) ? (
                          <Button
                            size="sm"
                            variant="danger"
                            onClick={() => unfollow(u._id)}
                          >
                            Unfollow
                          </Button>
                        ) : (
                          <Button
                            size="sm"
                            variant="success"
                            onClick={() => follow(u._id)}
                          >
                            Follow
                          </Button>
                        )}
                      </div>
                    )}
                  </ListGroup.Item>
                ))}
              </ListGroup>
            </Card.Body>
          </Card>

          <Row>
            {/* Followers */}
            <Col md={6} className="mb-3">
              <Card>
                <Card.Body>
                  <Card.Title>
                    Followers ({profile.followers?.length || 0})
                  </Card.Title>
                  <ListGroup>
                    {(profile.followers || []).map((f) => (
                      <ListGroup.Item
                        key={f._id || f}
                        className="d-flex justify-content-between align-items-center"
                      >
                        <span>{f.name || f.email}</span>
                      </ListGroup.Item>
                    ))}
                  </ListGroup>
                </Card.Body>
              </Card>
            </Col>

            {/* Following */}
            <Col md={6} className="mb-3">
              <Card>
                <Card.Body>
                  <Card.Title>
                    Following ({profile.following?.length || 0})
                  </Card.Title>
                  <ListGroup>
                    {(profile.following || []).map((f) => (
                      <ListGroup.Item
                        key={f._id || f}
                        className="d-flex justify-content-between align-items-center"
                      >
                        <span>{f.name || f.email}</span>
                        <Button
                          size="sm"
                          variant="danger"
                          onClick={() => unfollow(f._id || f)}
                        >
                          Unfollow
                        </Button>
                      </ListGroup.Item>
                    ))}
                  </ListGroup>
                </Card.Body>
              </Card>
            </Col>
          </Row>
        </Col>
      </Row>

      {/* BOTTOM – Your posts */}
      <Row className="mt-3">
        <Col>
          <Card>
            <Card.Body>
              <Card.Title>Your Posts</Card.Title>
              {myPosts.length === 0 && <p>No posts yet.</p>}
              <div className="d-flex flex-wrap gap-3 mt-2">
                {myPosts.map((p) => (
                  <div key={p._id} style={{ width: "170px" }}>
                    {p.image ? (
                      <img
                        src={`http://localhost:5000${p.image}`}
                        alt="post"
                        style={{
                          width: "100%",
                          height: "150px",
                          objectFit: "cover",
                          borderRadius: 8,
                        }}
                      />
                    ) : (
                      <div
                        style={{
                          width: "100%",
                          height: "150px",
                          borderRadius: 8,
                          border: "1px solid #ddd",
                          display: "flex",
                          alignItems: "center",
                          justifyContent: "center",
                          padding: 4,
                          fontSize: "0.85rem",
                          textAlign: "center",
                        }}
                      >
                        {p.caption || "No caption"}
                      </div>
                    )}
                  </div>
                ))}
              </div>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
};

export default Profile;
