import React, { useEffect, useState } from "react";
import { Card, Form, Button } from "react-bootstrap";
import api from "../api/axiosInstance";
import { useAuth } from "../context/AuthContext";
import { io } from "socket.io-client";

const socket = io("http://localhost:5000");

const ChatScreen = ({ selectedChat }) => {
  const { user } = useAuth();
  const [messages, setMessages] = useState([]);
  const [text, setText] = useState("");

  useEffect(() => {
    if (user?._id) {
      socket.emit("join", user._id);
    }
  }, [user]);

  useEffect(() => {
    if (!selectedChat) return;
    setMessages(selectedChat.messages || []);
  }, [selectedChat]);

  useEffect(() => {
    socket.on("receiveMessage", ({ from, content, chatId }) => {
      if (selectedChat && chatId === selectedChat._id) {
        setMessages((prev) => [...prev, { sender: { _id: from }, content }]);
      }
    });
    return () => {
      socket.off("receiveMessage");
    };
  }, [selectedChat]);

  if (!selectedChat) {
    return <Card.Body>Select a chat</Card.Body>;
  }

  const otherUser = selectedChat.users.find((u) => u._id !== user._id);

  const sendMessage = async (e) => {
    e.preventDefault();
    if (!text.trim()) return;

    await api.post(`/chats/${selectedChat._id}/messages`, {
      content: text,
    });

    socket.emit("sendMessage", {
      from: user._id,
      to: otherUser._id,
      content: text,
    });

    setMessages((prev) => [
      ...prev,
      { sender: { _id: user._id }, content: text },
    ]);
    setText("");
  };

  return (
    <Card style={{ height: "400px", display: "flex", flexDirection: "column" }}>
      <Card.Header>Chat with {otherUser?.name}</Card.Header>
      <Card.Body style={{ overflowY: "auto" }}>
        {messages.map((m, idx) => (
          <div
            key={idx}
            style={{
              textAlign: m.sender?._id === user._id ? "right" : "left",
            }}
          >
            <span
              style={{
                display: "inline-block",
                padding: "4px 8px",
                margin: "2px 0",
                borderRadius: 8,
                background:
                  m.sender?._id === user._id ? "#d1e7dd" : "#e2e3e5",
              }}
            >
              {m.content}
            </span>
          </div>
        ))}
      </Card.Body>
      <Card.Footer>
        <Form onSubmit={sendMessage} className="d-flex gap-2">
          <Form.Control
            value={text}
            onChange={(e) => setText(e.target.value)}
            placeholder="Type a message"
          />
          <Button type="submit">Send</Button>
        </Form>
      </Card.Footer>
    </Card>
  );
};

export default ChatScreen;
