import React, { useState, useEffect } from "react";
import { Card, Container, Form, Button, Image } from "react-bootstrap";
import api from "../api/axiosInstance";
import { useAuth } from "../context/AuthContext";

const Profile = () => {
  const { user } = useAuth();
  const [profile, setProfile] = useState(null);
  const [name, setName] = useState("");
  const [bio, setBio] = useState("");
  const [qr, setQr] = useState("");
  const [otp, setOtp] = useState("");
  const [message, setMessage] = useState("");
  const [myPosts, setMyPosts] = useState([]);

  const fetchProfile = async () => {
    const { data } = await api.get("/users/me");
    setProfile(data);
    setName(data.name || "");
    setBio(data.bio || "");

    // get my posts
    const postsRes = await api.get(`/posts/user/${data._id}`);
    setMyPosts(postsRes.data);
  };

  useEffect(() => {
    fetchProfile();
    // eslint-disable-next-line
  }, []);

  const updateProfile = async (e) => {
    e.preventDefault();
    const { data } = await api.put("/users/me", { name, bio });
    setProfile(data);
    setMessage("Profile updated");
  };

  const handleFileChange = async (e) => {
    const file = e.target.files[0];
    if (!file) return;
    const formData = new FormData();
    formData.append("image", file);
    const { data } = await api.post("/users/me/profile-pic", formData, {
      headers: { "Content-Type": "multipart/form-data" },
    });
    setProfile((prev) => ({ ...prev, profilePic: data.profilePic }));
  };

  const generate2FA = async () => {
    const { data } = await api.post("/auth/2fa/generate");
    setQr(data.qrDataUrl);
  };

  const enable2FA = async () => {
    const { data } = await api.post("/auth/2fa/enable", { otp });
    setMessage(data.message);
    fetchProfile();
  };

  if (!profile) return <Container>Loading...</Container>;

  return (
    <Container style={{ maxWidth: "600px" }}>
      <Card>
        <Card.Body>
          <Card.Title>Profile</Card.Title>
          {message && <div className="text-success mb-2">{message}</div>}

          {/* Followers / Following count */}
          <div className="mb-3">
            <strong>{profile.name}</strong>
            <div>
              <span className="me-3">
                Followers: {profile.followers ? profile.followers.length : 0}
              </span>
              <span>
                Following: {profile.following ? profile.following.length : 0}
              </span>
            </div>
          </div>

          <div className="mb-3 d-flex align-items-center gap-3">
            {profile.profilePic && (
              <Image
                src={`http://localhost:5000${profile.profilePic}`}
                roundedCircle
                width={80}
                height={80}
              />
            )}
            <Form.Group>
              <Form.Label>Profile Picture</Form.Label>
              <Form.Control type="file" onChange={handleFileChange} />
            </Form.Group>
          </div>

          <Form onSubmit={updateProfile}>
            <Form.Group className="mb-3">
              <Form.Label>Name</Form.Label>
              <Form.Control
                value={name}
                onChange={(e) => setName(e.target.value)}
              />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>Bio</Form.Label>
              <Form.Control
                as="textarea"
                rows={3}
                value={bio}
                onChange={(e) => setBio(e.target.value)}
              />
            </Form.Group>
            <Button type="submit" className="mb-3">
              Save
            </Button>
          </Form>

          {/* My posts grid */}
          <hr />
          <h5 className="mt-3 mb-2">My Posts</h5>
          {myPosts.length === 0 && <p>No posts yet.</p>}
          <div className="d-flex flex-wrap gap-3">
            {myPosts.map((p) => (
              <div key={p._id} style={{ width: "150px" }}>
                {p.image ? (
                  <img
                    src={`http://localhost:5000${p.image}`}
                    alt="post"
                    style={{
                      width: "100%",
                      height: "150px",
                      objectFit: "cover",
                      borderRadius: 8,
                    }}
                  />
                ) : (
                  <div
                    style={{
                      width: "100%",
                      height: "150px",
                      borderRadius: 8,
                      border: "1px solid #ddd",
                      display: "flex",
                      alignItems: "center",
                      justifyContent: "center",
                      padding: 4,
                      fontSize: "0.85rem",
                      textAlign: "center",
                    }}
                  >
                    {p.caption || "No caption"}
                  </div>
                )}
              </div>
            ))}
          </div>

          <hr />
          <h5>Two Factor Authentication</h5>
          <p>Enabled: {profile.twoFactorEnabled ? "Yes" : "No"}</p>
          {!profile.twoFactorEnabled && (
            <>
              <Button onClick={generate2FA} className="mb-3">
                Generate QR
              </Button>
              {qr && (
                <div className="mb-3">
                  <p>Scan this QR in Google Authenticator:</p>
                  <img src={qr} alt="2FA QR" style={{ maxWidth: "200px" }} />
                </div>
              )}
              {qr && (
                <div className="mb-3">
                  <Form.Label>Enter OTP after scanning</Form.Label>
                  <Form.Control
                    value={otp}
                    onChange={(e) => setOtp(e.target.value)}
                  />
                  <Button onClick={enable2FA} className="mt-2">
                    Enable 2FA
                  </Button>
                </div>
              )}
            </>
          )}
        </Card.Body>
      </Card>
    </Container>
  );
};

export default Profile;
